import React, { Fragment } from 'react';
import { Row,
         Form, 
         Input, 
         Button, 
         Checkbox, 
         Col, 
         Card, 
         DatePicker, 
         InputNumber,
         Divider
         
} from 'antd';

import { useSelector } from 'react-redux';

import { uploadData } from '../../functions/upload';

const Upload = () => {

    const { user } = useSelector((state) => ({ ...state }))
    console.log('user', user);

    const onFinish = (values) => {
        //console.log('Success:', values);
        uploadData(values, user.token)
            .then( res => {
                console.log(res);
            }).catch
    };
    
    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };

    return (

    <>  
        <Row justify="center">
            <Card>
                <Form
                    name="basic"
                    labelCol={{
                        span: 8,
                    }}
                    wrapperCol={{
                        span: 16,
                    }}
                    initialValues={{
                        remember: true,
                    }}
                    onFinish={onFinish}
                    onFinishFailed={onFinishFailed}
                    autoComplete="off"
                >

                    <Form.Item
                        label="วันที่"
                        name="ulDate"
                        rules={[
                            {
                                required: true,
                                message: 'กรุณากรอกวันที่!',
                            },
                            ]}
                    >
                        <DatePicker />
                    </Form.Item>

                    <Form.Item
                        label="เลขรับ"
                        name="UlInID"
                        rules={[
                            {
                                required: true,
                            },
                            ]}
                    >
                        <Input />
                    </Form.Item>


                    <Form.Item
                        label="ที่"
                        name="UlLocate"
                        rules={[
                            {
                                required: true,
                                message: 'ระบุที่',
                            },
                        ]}
                    >
                        <Input />
                    </Form.Item>


                    <Form.Item
                        label="ลงวันที่"
                        name="UlDateGen"
                        rules={[
                            {
                                required: true,
                            },
                        ]}
                >
                        <DatePicker />
                    </Form.Item>

                    <Form.Item
                        label="จาก"
                        name="UlFrom"
                        rules={[
                            {
                                required: true,
                                message: "ระบุจาก",
                            },
                        ]}
                    >
                        <Input />
                    </Form.Item>

                    <Form.Item
                        label="ถึง"
                        name="UlTo"
                        rules={[
                            {
                                required: true,
                                message: "ระบุถึง",
                            },
                        ]}
                    >
                        <Input />
                    </Form.Item>


                    <Form.Item
                        label="เรื่อง"
                        name="UlTitle"
                        rules={[
                            {
                                required: true,
                                message: 'ระบุเรื่อง',
                            },
                        ]}
                    >
                        <Input />
                    </Form.Item>

                                        

                    <Form.Item
                        wrapperCol={{
                                offset: 8,
                                span: 16,
                        }}
                    >
                        <Button type="primary" htmlType="submit">
                        Submit
                        </Button>
                    </Form.Item>

                </Form>

            </Card>
        </Row>
    </>             
        
    );
}

export default Upload;
