import React, { useState, useEffect } from 'react';
import SearchBox from '../../../components/UserComponent/SearchBox/SearchBox';
import { ConfigProvider, DatePicker, Button, Table, Radio, Image, Tag, Space } from 'antd';
import locale from 'antd/lib/locale/th_TH';
import { AiOutlineVerticalAlignBottom} from "react-icons/ai";
import DownloadLink from "react-download-link";
import { getInSubj } from '../../../functions/insubj';
import { useSelector } from 'react-redux';
import { toast } from 'react-toastify';
import { PlusOutlined } from '@ant-design/icons';
import '../upload1.css';
import { Link } from 'react-router-dom';
import { getDocByMonthInPerson, getDocByDayInPerson, getDocByYearInPerson, 
    getDocByDayOutPerson, getDocByMonthOutPerson, getDocByYearOutPerson } from '../../../functions/summary';


const Summary = () => {
    const [searchText, setSearchText] = useState('');
    const [searchNo, setSearchNo] = useState('');
    const [searchFrom, setSearchFrom] = useState('');
    const { RangePicker } = DatePicker;
    const [hackValue, setHackValue] = useState();
    const [dates, setDates] = useState([]);
    const [value, setValue] = useState();
    const [person, setPerson] = useState([]);
    const { user } = useSelector((state) => ({ ...state }))
    const { Column, ColumnGroup } = Table;
    

    useEffect(() => {
        loadPerson(user.token);
        
        getDocByDayInPerson(user.token)
            .then((res) => {
                console.log(res.data)

            }).catch((err) => {
                console.log(err)
            })
        getDocByMonthInPerson(user.token)
            .then((res) => {
                console.log(res.data)
            }).catch((err) => {
                console.log(err)
            })

        getDocByYearInPerson(user.token)
            .then((res) => {
                console.log(res.data)
            }).catch((err) => {
                console.log(err)
            })



        getDocByDayOutPerson(user.token)
            .then((res) => {
                console.log(res.data)

            }).catch((err) => {
                console.log(err)
            })
        getDocByMonthOutPerson(user.token)
            .then((res) => {
                console.log(res.data)
            }).catch((err) => {
                console.log(err)
            })

        getDocByYearOutPerson(user.token)
            .then((res) => {
                console.log(res.data)
            }).catch((err) => {
                console.log(err)
            })



            
      }, [])
    
      const loadPerson = (authtoken) => {
        getInSubj(authtoken)
            .then((res) => {
                setPerson(res.data)
                console.log(res.data)
            }).catch((err) => {
                toast.error(err)
                console.log(err)
            })
    }



    const disabledDate = current => {
        if (!dates || dates.length === 0) {
          return false;
        }
        const tooLate = dates[0] && current.diff(dates[0], 'days') > 7;
        const tooEarly = dates[1] && dates[1].diff(current, 'days') > 7;
        return tooEarly || tooLate;
      };


      const onOpenChange = open => {
        if (open) {
          setHackValue([]);
          setDates([]);
        } else {
          setHackValue(undefined);
        }
      };
      const data = [
        {
          key: '1',
          name: 'ด้านบริหารและธุรการ(ทั่วไป)',
          countIn1: '35',
          countIn30: '1',
          countIn365: '1',
          countOut1: '35',
          countIn30: '1',
          countIn365: '1',
          
          
          
        },
        {
          key: '2',
          name: 'ด้านการเงิน',
          countIn1: '0',
          countIn30: '1',
          countIn365: '1',
          
          
        },
        {
          key: '3',
          name: 'ด้านพัสดุ',
          countIn1: '0',
          countIn30: '1',
          countIn365: '1',
          
          
        },
        {
            key: '4',
            name: 'ด้านวิจัย',
            countIn1: '0',
            countIn30: '1',
            countIn365: '1',
            
            
          },
        {
          key: '5',
          name: 'ด้านแผนยุทธศาสตร์',
          countIn1: '0',
          countIn30: '1',
          countIn365: '1',
          
          
        },
        {
            key: '6',
            name: 'ด้านทรัพยากรบุคคล',
            countIn1: '0',
            countIn30: '1',
            countIn365: '1',
            
            
        },
        {
            key: '7',
            name: 'ด้านการบริการการศึกษา',
            countIn1: '0',
            countIn30: '1',
            countIn365: '1',
            
            
        },
        {
            key: '8',
            name: 'ด้านอาคารสถานที่',
            countIn1: '0',
            countIn30: '1',
            countIn365: '1',
            
            
        },
        {
            key: '9',
            name: 'สาขาวิชาต่างๆ',
            countIn1: '0',
            countIn30: '1',
            countIn365: '1',
            
            
        },
        {
            key: '10',
            countIn1: '0',
            countIn30: '1',
            countIn365: '1',
            name: 'คำสั่งประกาศ',
       
        },
        {
            key: '11',
            name: 'ระเบียบข้อบังคับ',
            countIn1: '0',
            countIn30: '1',
            countIn365: '1',
            
            
        },
      ];

      

  return <div>
    <Table dataSource={data} size="small">
    <Column title="หมวดหมู่" dataIndex="name" key="name" />
    <ColumnGroup title="วันนี้">
      <Column title="รับเข้า" dataIndex="firstName" key="firstName" />
      <Column title="ส่งออก" dataIndex="lastName" key="lastName" />
    </ColumnGroup>

    <ColumnGroup title="เดือนนี้">
      <Column title="รับเข้า" dataIndex="firstName" key="firstName" />
      <Column title="ส่งออก" dataIndex="lastName" key="lastName" />
    </ColumnGroup>

    <ColumnGroup title="ปีนี้">
      <Column title="รับเข้า" dataIndex="firstName" key="firstName" />
      <Column title="ส่งออก" dataIndex="lastName" key="lastName" />
    </ColumnGroup>


  </Table>
        
      
        
      </div>;
};

export default Summary
