import React, { useState, useEffect } from 'react';
import DownloadLink from "react-download-link";
import { useSelector } from 'react-redux';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Table, Avatar, Image } from 'antd';
import "../upload.css";
import { createOutReso, getOutReso, removeOutReso } from '../../../functions/outreso';
import { DeleteOutlined, VerifiedOutlined } from '@ant-design/icons';
import { AiOutlineVerticalAlignBottom} from "react-icons/ai";
import { Document,Page } from 'react-pdf/dist/esm/entry.webpack';

const UpForm2 = () => {
    const { user } = useSelector((state) => ({ ...state }))
    const [name, setName] = useState("");
    const [loading, setLoading] = useState(false);
    const [person, setPerson] = useState([]);
    const [ file, setFile] = useState();
    const [ filename, setFilename] = useState('')
    const [ dateFirst, setDateFirst] = useState('');
    const [ numTo, setNumTo] = useState('');
    const [ locate, setLocate ] = useState('');
    const [ dateGen, setDateGen] = useState('');
    const [ from, setFrom] = useState('');
    const [ to, setTo] = useState('');
    const [numPages, setNumPages] = useState(null);
    const [pageNumber, setPageNumber] = useState(1);


    useEffect(() => {
        loadPerson(user.token);
       
    }, [])

   
    const loadPerson = (authtoken) => {
        getOutReso(authtoken)
            .then((res) => {
                setPerson(res.data)
            }).catch((err) => {
                toast.error(err)
                console.log(err)
            })
    }

    const onSubmit = (e) => {
        e.preventDefault();
        setLoading(true);
        const formData = new FormData();
        
        formData.append('file', file);
        formData.append('data', name)
        formData.append('data', dateFirst)
        formData.append('data', numTo)
        formData.append('data', locate)
        formData.append('data', dateGen)
        formData.append('data', from)
        formData.append('data', to)



        
        createOutReso(formData, user.token)
            .then(res=>{
                loadPerson(user.token);
                setLoading(false)
                toast.success('อัพโหลดไฟล์ '+ res.data.name + ' สำเร็จ ')
            }).catch(err=>{
                setLoading(false)
                toast.error(err.response)
            })
    }

    const handleRemove = (id) => {
        if (window.confirm("คุณแน่ใจว่าจะลบไฟล์!")){
            setLoading(true);
            removeOutReso(id, user.token)
                .then(res=>{
                    loadPerson(user.token);
                    setLoading(false)
                    toast.success('ลบไฟล์ '+ res.data.name + ' สำเร็จ ')
                }).catch(err=>{
                    setLoading(false)
                    toast.error(err.response)
                })
        }
    }

    const columns = [

        {
            title: 'วันที่',
            dataIndex: 'dateFirst',
            key: 'dateFirst'
        },
        {
            title: 'เลขรับ',
            dataIndex: 'numTo',
            key: 'numTo'
        },
        {
            title: 'ที่',
            dataIndex: 'locate',
            key: 'locate'
        },
        {
            title: 'ลงวันที่',
            dataIndex: 'dateGen',
            key: 'dateGen'
        },
        {
            title: 'จาก',
            dataIndex: 'from',
            key: 'from'
        },
        {
            title: 'ถึง',
            dataIndex: 'to',
            key: 'to'
        },
        {
            title: 'Download',
            render: (record) => (
                <DownloadLink className="button-field-download"  filename={record.pic} label="Download" >
                   
                    <AiOutlineVerticalAlignBottom className="button-icon-download"/>          
                    <div className="button-text-download">
                    ดาวน์โหลด</div>
                </DownloadLink>
            )
        },
        {
            title: 'เรื่อง',
            dataIndex: 'name',
            key: 'name'
        },
        {
            title: 'ไฟล์',
            render: (record) => (
                <>
                
                    <a href={`http://localhost:5000/uploads/${record.pic}`} target="_blank" >
                    <Image src={`http://localhost:5000/uploads/${record.pic}`} />  
                    </a>
                </>
            )
        },
        {
            title: 'Actions',
            render: (record) => (
                <span className="btn btn-sm fload-right"
                onClick={()=> handleRemove(record._id)}>
                    <DeleteOutlined className="text-danger"/>
                </span>
            )
        }

    ]

    function onDocumentLoadSuccess({numPages}){
        setNumPages(numPages);
        setPageNumber(1);
      }
    
      function changePage(offSet){
        setPageNumber(prevPageNumber => prevPageNumber + offSet);
      }
    
      function changePageBack(){
        changePage(-1)
      }
    
      function changePageNext(){
        changePage(+1)
      }
    


    return (
        
        <div className="container-fluid">
            
            <div >
                <div className="headfrom">
                เพิ่มข้อมูลรับเข้าเอกสาร
                </div>
                <div className="previewPDF">
                    <Document file={file} onLoadSuccess={onDocumentLoadSuccess}>
                        <Page height="800" pageNumber={pageNumber} />
                        </Document>
                </div>
                <div className="realfrom">
                    <form onSubmit={onSubmit} className="allform">
                        
                    <div className="file-input">
                            <input
                                type="file"
                                className="file" 
                                onChange={ (e) => setFile(e.target.files[0]) }                                                                
                            />
                         
                            <label className="file-name"
                                htmlFor="customfile"
                            >
                                {filename} 
                            </label>
                        </div>

                

                        <div className="dateinput">
                            <label>วันที่</label>
                            <input type="date"
                                className="date-input"
                                autoFocus
                                required
                                onChange={(e) => setDateFirst(e.target.value)}
                            />
                        </div>

                        <div className="noinput">
                            <label>เลขรับ</label>
                            <input type="number"
                                className="no"
                                autoFocus
                                required
                                onChange={(e) => setNumTo(e.target.value)}
                            />
                        </div>

                        <div className="nofrominput">
                            <label>ที่</label>
                            <input type="text"
                                className="nofrom"
                                autoFocus
                                required
                                onChange={(e) => setLocate(e.target.value)}
                            />
                        </div>


                        <div className="inputdate">
                            <label>ลงวันที่</label>
                            <input type="date"
                                className="datedate"
                                autoFocus
                                required
                                onChange={(e) => setDateGen(e.target.value)}
                            />
                        </div>
                    
                        <div className="frominput">
                            <label>จาก</label>
                            <input type="text"
                                className="formbox"
                                autoFocus
                                required
                                onChange={(e) => setFrom(e.target.value)}
                            />
                        </div>

                        <div className="tooinput">
                            <label>ถึง</label>
                            <input type="text"
                                className="toobox"
                                autoFocus
                                required
                                onChange={(e) => setTo(e.target.value)}
                            />
                        </div>
                        <div className="nameinput">
                            <label>เรื่อง</label>
                            <input type="text"
                                className="namebox"
                                autoFocus
                                required
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>

                        <button className="btnfrom">เพิ่ม</button>
                    </form>

                </div>
                
            </div>
            
        </div>
        
    )
}

export default UpForm2
