import React from 'react'
import { Form, 
    Input, 
    Button, 
    Row,
    Col,
    Card
} from 'antd';

import { loginHandler } from '../../functions/auth';

const Login = ({ history }) => {
    const roleBaseRedirect = (role) => {
        if(role === 'admin') {
            history.push('/home-admin')
        } else {
            history.push('/home-user')
        }
    }

    const onFinish = (values) => {
            
            console.log('Success', values);
            loginHandler(values)
            .then(res => {
                roleBaseRedirect()
                console.log(res.data.payload.user.role)
                alert(res.data)
                history.push('/login')
            }).catch(err => {
                console.log(err.response.data)
                alert(err.response.data)
            })
          
        };
        
        const onFinishFailed = (errorInfo) => {
            console.log('Failed:', errorInfo);
          };

    return (
        <Row justify="center">
            <Col>
                <Card>
                    <Form
                        name="basic"
                        labelCol={{
                            span: 8,
                        }}
                        wrapperCol={{
                            span: 16,
                        }}
                        initialValues={{
                            remember: true,
                        }}
                        onFinish={onFinish}
                        onFinishFailed={onFinishFailed}
                        autoComplete="off"
                        >
                    <Form.Item
                        label="Username"
                        name="name"
                        rules={[
                        {
                            required: true,
                            message: 'Please input your username!',
                        },
                        ]}
                    >
                        <Input />
                    </Form.Item>

                    <Form.Item
                        label="Password"
                        name="password"
                        rules={[
                        {
                            required: true,
                            message: 'Please input your password!',
                        },
                        ]}
                    >
                        <Input.Password />
                    </Form.Item>
  

                    <Form.Item
                        wrapperCol={{
                        offset: 8,
                        span: 16,
                        }}
                    >
                        <Button type="primary" htmlType="submit">
                        Submit
                        </Button>
                    </Form.Item>
                    </Form>
                </Card>
            </Col>
        </Row>
    )
}

export default Login
