            .then((res) => {
                console.log(res.data)

            }).catch((err) => {
                console.log(err)
            })
        getDocByMonthOutPerson(user.token)
            .then((res) => {
                console.log(res.data)
            }).catch((err) => {
                console.log(err)
            })

        getDocByYearOutPerson(user.token)
            .then((res) => {
                console.log(res.data)
            }).catch((err) => {
                console.log(err)
            })



            
      }, [])
    
      const loadPerson = (authtoken) => {
        getInSubj(authtoken)
            .then((res) => {
                setPerson(res.data)
                console.log(res.data)
            }).catch((err) => {
                toast.error(err)
                console.log(err)
            })
    }



    const disabledDate = current => {
        if (!dates || dates.length === 0) {
          return false;
        }
        const tooLate = dates[0] && current.diff(dates[0], 'days') > 7;
        const tooEarly = dates[1] && dates[1].diff(current, 'days') > 7;
        return tooEarly || tooLate;
      };


      const onOpenChange = open => {
        if (open) {
          setHackValue([]);
          setDates([]);
        } else {
          setHackValue(undefined);
        }
      };
      const data = [
        {
          key: '1',
          firstName: 'John',
          lastName: 'Brown',
          age: 32,
          address: 'New York No. 1 Lake Park',
          tags: ['nice', 'developer'],
        },
        {
          key: '2',
          firstName: 'Jim',
          lastName: 'Green',
          age: 42,
          address: 'London No. 1 Lake Park',
          tags: ['loser'],
        },
        {
          key: '3',
          firstName: 'Joe',
          lastName: 'Black',
          age: 32,
          address: 'Sidney No. 1 Lake Park',
          tags: ['cool', 'teacher'],
        },
      ];

      

  return <div>
      
        
      <div><Table rowKey={rows}    columns={columns}  className="upload-table" defaultPageSize= "2"/></div>
        
      </div>;
};

export default Summary
