import React, { useState, useEffect } from 'react';
import DocUpload from './DocUpload';
import AdminNav from '../../layout/AdminNav';
import { useSelector } from 'react-redux';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Table, Avatar, Image } from 'antd';

import { createPerson, getPerson, removePerson } from '../../functions/person';
import { DeleteOutlined } from '@ant-design/icons';

const AdminCreatePerson = () => {
    const { user } = useSelector((state) => ({ ...state }))

    const [name, setName] = useState("");
    const [loading, setLoading] = useState(false);
    const [person, setPerson] = useState([]);

    const [ file, setFile] = useState('');
    const [ filename, setFilename] = useState('')

    const [ dateFirst, setDateFirst] = useState('');
    const [ numTo, setNumTo] = useState('');
    const [ locate, setLocate ] = useState('');
    const [ dateGen, setDateGen] = useState('');
    const [ from, setFrom] = useState('');
    const [ to, setTo] = useState('');
    

    useEffect(() => {
        loadPerson(user.token);
    }, [])
    const loadPerson = (authtoken) => {
        getPerson(authtoken)
            .then((res) => {
                setPerson(res.data)
            }).catch((err) => {
                toast.error(err)
                console.log(err)
            })
    }

    const onSubmit = (e) => {
        e.preventDefault();
        setLoading(true);
        const formData = new FormData();
        formData.append('file', file);
        formData.append('data', name)


        
        createPerson(formData, user.token)
            .then(res=>{
                loadPerson(user.token);
                setLoading(false)
                toast.success('Create '+ res.data.name + ' Success ')
            }).catch(err=>{
                setLoading(false)
                toast.error(err.response)
            })
    }

    const handleRemove = (id) => {
        if (window.confirm("Are you sure Delete!")){
            setLoading(true);
            removePerson(id, user.token)
                .then(res=>{
                    loadPerson(user.token);
                    setLoading(false)
                    toast.success('Remove '+ res.data.name + ' Success ')
                }).catch(err=>{
                    setLoading(false)
                    toast.error(err.response)
                })
        }
    }

    const columns = [
        {
            title: 'ชื่อ',
            dataIndex: 'name',
            key: 'name'
        },
        {
            title: 'ไฟล์',
            render: (record) => (
                <>
                
                    <Avatar 
                        src={<Image src={`http://localhost:5000/uploads/${record.pic}`}  />}  
                    />
                </>
            )
        },
        {
            title: 'Actions',
            render: (record) => (
                <span className="btn btn-sm fload-right"
                onClick={()=> handleRemove(record._id)}>
                    <DeleteOutlined className="text-danger"/>
                </span>
            )
        }

    ]


    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-2">
                    <AdminNav/>
                </div>
                <div className="col">
                    {loading
                    ? <h4>Loading ...</h4>                         
                    : <h4>ระบบส่งออกข้อมูลเอกสาร คณะวิทยาการคอมพิวเตอร์</h4>}
                    
                    <form onSubmit={onSubmit}>
                    <div className="custom-file mb-4">
                            <input
                                type="file"
                                className="custom-file-input" 
                                onChange={(e) => setFile(e.target.files[0])}                  
                            />
                         
                            <label className="custom-file-label"
                                htmlFor="customfile"
                            >
                                {filename} 
                            </label>
                        </div>

                <div className="col-4">

                        <div className="form-group">
                            <label>วันที่</label>
                            <input type="date"
                                className="form-control"
                                autoFocus
                                required
                                onChange={(e) => setDateFirst(e.target.value)}
                            />
                        </div>

                        <div className="form-group">
                            <label>เลขส่งออก</label>
                            <input type="number"
                                className="form-control"
                                autoFocus
                                required
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>

                        <div className="form-group">
                            <label>ที่</label>
                            <input type="text"
                                className="form-control"
                                autoFocus
                                required
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>


                        <div className="form-group">
                            <label>ลงวันที่</label>
                            <input type="date"
                                className="form-control"
                                autoFocus
                                required
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>

                






                        <div className="form-group">
                            <label>จาก</label>
                            <input type="text"
                                className="form-control"
                                autoFocus
                                required
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>

                        <div className="form-group">
                            <label>ถึง</label>
                            <input type="text"
                                className="form-control"
                                autoFocus
                                required
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>










                        <div className="form-group">
                            <label>เรื่อง</label>
                            <input type="text"
                                className="form-control"
                                autoFocus
                                required
                                onChange={(e) => setName(e.target.value)}
                            />



                        </div>

               </div>

                       


                    
                        <hr />

                        <button className="btn btn-outline-primary">เพิ่ม</button>
                    </form>
                    <hr />
                    <Table columns={columns} dataSource={person} rowKey="_id"/>


                </div>
            </div>
        </div>
    )
}

export default AdminCreatePerson
