import React, { useState } from 'react';
import { Row,
         Form, 
         Input, 
         Button, 
         Checkbox, 
         Col, 
         Card, 
         DatePicker, 
         InputNumber,
         Divider
         
} from 'antd';

import { DownOutlined, UpOutlined } from '@ant-design/icons';

import './upload.css'
const Upload = () => {
    
    const [expand, setExpand] = useState(false);
    const [form] = Form.useForm();


    const getFields = () => {
        const count = expand ? 10 : 6;
        const children = [];

        for (let i = 0; i < count; i++) {
          children.push(
            <Col span={8} key={i}>
              <Form.Item
                name={`field-${i}`}
                label={`Field ${i}`}
                rules={[
                  {
                    required: true,
                    message: 'Input something!',
                  },
                ]}
              >
                <Input placeholder="placeholder" />
              </Form.Item>
            </Col>,
          );
        }
        return children;
      };


    const onFinish = (values) => {
        console.log('Received values of form: ', values);
    };




    return (
        <Form
        name="basic"
        labelCol={{
            span: 8,
        }}
        wrapperCol={{
            span: 16,
        }}
        initialValues={{
            remember: true,
        }}
        onFinish={onFinish}
        onFinishFailed={onFinishFailed}
        autoComplete="off"
        >
        <Form.Item
            label="วันที่"
            name="ulDate"
            rules={[
            {
                required: true,
                message: 'กรุณากรอกวันที่!',
            },
            ]}
        >
            <DatePicker />
        </Form.Item>

        <Form.Item
            label="เลขรับ"
            name="UlInID"
            rules={[
            {
                required: true,
            },
            ]}
        >
            <Input />
        </Form.Item>


        <Form.Item
            label="ที่"
            name="UlLocate"
            rules={[
            {
                required: true,
                message: 'ระบุที่',
            },
            ]}
        >
            <Input />
        </Form.Item>


        <Form.Item
            label="ลงวันที่"
            name="UlDateGen"
            
            rules={[
            {
                required: true,
            },
            ]}
        >
            <DatePicker />
        </Form.Item>

        <Form.Item
            label="จาก"
            name="UlFrom"
            rules={[
            {
                required: true,
                message: "ระบุจาก",
            },
            ]}
        >
            <Input />
        </Form.Item>
        <Form.Item
            label="ถึง"
            name="UlTo"
            rules={[
            {
                required: true,
                message: "ระบุถึง",
            },
            ]}
        >
            <Input />
        </Form.Item>


        <Form.Item
            label="เรื่อง"
            name="UlTitle"
            rules={[
            {
                required: true,
                message: 'ระบุเรื่อง',
            },
            ]}
        >
            <Input />
        </Form.Item>

        

        <Form.Item
            wrapperCol={{
            offset: 8,
            span: 16,
            }}
        >
            <Button type="primary" htmlType="submit">
            Submit
            </Button>
        </Form.Item>
    </Form>



        

        
    )
    
}
export default Upload
